#include<iostream>#include<cmath>#include<string>#define INT_MAX 2147483647#define INT_MIN -2147483648using namespace std;int myAtoi(string str) {	long long x = 0;	bool flags = false;	int num1 = 0 , num2 = 0;	string s  = "", sum = ""; 	if (str == "")	{		return 0;	}	for (int i = 0; i < str.size(); ++i)	{		if (str[i] != ' ')		{			s += str[i];			if (str[i+1] == ' ')			{				break;			}		}	}	for (int i = 0; i < s.size(); ++i)	{		if (s[i] == '+'){			num1++;			continue;		}		if (s[i] == '-')		{			num2++;			flags = true;			continue;		}		if ( '0' <= s[i]  && '9' >= s[i]){			sum += s[i];		}		if (s[i] > '9')		{			break;		}	}	if (sum.size() > 10 && flags == false)	{		return 2147483647;	}else if (sum.size() > 10 && flags == true){		return -2147483648;	}	int len = sum.size() -1;	int temp = 0;	int start = 0;	for (int i = start; i < sum.size(); ++i,--len)	{		if (num2 -num1 ==0 && num1 != 0 )		{			return 0;		}		if (abs(num2-num1) > 1)		{			return 0;		}		temp = (sum[i]-48) * pow(10 , len);		x += temp;	}	if ( x > 2147483647 )	{		if (flags == false)		{			return 2147483647 ;		}else{			return -2147483648;		}	}	if (flags == true){		return -x;	}else {		return x;	} } int main(int argc, char const *argv[]) { 	string string = "      -11919730356x"; 	cout<<myAtoi(string)<<endl; 	return 0; }